const BackOfficeHeader = () => {
  const [selectedName, setSelectedName] = useState("Funds");

  const handleSelect = (name) => {
    setSelectedName(name);
  };

  return (
    <div className="swift-back-office-header">
      <div className="swift-back-office-header-row">
        <div
          className={`swift-back-office-header-icon-wrapper ${
            selectedName === "Funds" ? "active" : ""
          }`}
          onClick={() => handleSelect("Funds")}
        >
          <Funds color={selectedName === "Funds" ? "white" : "black"} />
        </div>
        <p className="swift-back-office-header-row-name">Funds</p>
      </div>

      <div className="swift-back-office-header-row">
        <div
          className={`swift-back-office-header-icon-wrapper ${
            selectedName === "Manage Requests" ? "active" : ""
          }`}
          onClick={() => handleSelect("Manage Requests")}
        >
          <ManageRequest
            color={selectedName === "Manage Requests" ? "white" : "black"}
          />
        </div>
        <p className="swift-back-office-header-row-name">Manage Requests</p>
      </div>

      <div className="swift-back-office-header-row">
        <div
          className={`swift-back-office-header-icon-wrapper ${
            selectedName === "Accounts" ? "active" : ""
          }`}
          onClick={() => handleSelect("Accounts")}
        >
          <Bag color={selectedName === "Accounts" ? "white" : "black"} />
        </div>
        <p className="swift-back-office-header-row-name">Accounts</p>
      </div>
    </div>
  );
};

export default BackOfficeHeader;
